const data = $input.all().map(item => item.json);

// If there are no records, return a message instead
if (data.length === 0) {
    return [{ html: "<p>No recent activity in the past 28 hours.</p>" }];
}

// Define table headers
let htmlTable = `
<table border="1" cellpadding="5" cellspacing="0" style="border-collapse: collapse; width: 100%;">
  <thead>
    <tr>
      <th>Property</th>
      <th>Current Occupancy</th>
      <th>Future Occupancy</th>
      <th>Leases Needed 90%</th>
      <th>Interests</th>
      <th>Showings</th>
      <th>Applications</th>
      <th>Leases</th>
      <th>Vacants</th>
      <th>Expirations</th>
      <th>Future Move-Ins</th>
      <th>On Notice</th>
    </tr>
  </thead>
  <tbody>`;

// Loop through the data and populate the rows
data.forEach(row => {
    htmlTable += `
    <tr>
      <td>${row.property}</td>
      <td>${row.current_occupancy}%</td>
      <td>${row.future_occupancy}%</td>
      <td>${row.leases_needed_90}</td>
      <td>${row.interests}</td>
      <td>${row.showings}</td>
      <td>${row.applications}</td>
      <td>${row.leases}</td>
      <td>${row.vacants}</td>
      <td>${row.expirations}</td>
      <td>${row.future_move_ins}</td>
      <td>${row.on_notice}</td>
    </tr>`;
});

htmlTable += `</tbody></table>`;

// Return formatted table
return [{ html: htmlTable }];
