//
// 1) Get the data from the previous node
//
let allItems = $input.all();

// Try the typical scenario: allItems[0].json is the array of rows
let rows = allItems[0].json;

// If that didn’t work, your data might be in allItems[0].json.data
// or each row might be in a separate item. Adjust as needed.
if (!Array.isArray(rows)) {
  // Maybe it's in allItems[0].json.data?
  if (allItems[0].json.data && Array.isArray(allItems[0].json.data)) {
    rows = allItems[0].json.data;
  } else {
    // Possibly each item is a row
    rows = allItems.map(item => item.json).flat();
  }
}

// If it's still not an array, throw an error
if (!Array.isArray(rows)) {
  throw new Error(
    "We couldn’t find an array in the expected paths. Please check the Extract from File node output."
  );
}

//
// 2) Initialize a data map to track guest card counts
//
const guestCardMap = {};

//
// 3) Loop over each row
//
rows.forEach(row => {
  // Make sure the row has a Property ID
  if (!row.hasOwnProperty("Property ID")) return;

  const propertyId = String(row["Property ID"]).trim();
  if (!propertyId) return;

  // Read & normalize the status
  const status = String(row["Status"] || "").toLowerCase().trim();

  // If the status is "inactive," we skip it entirely
  if (status === "inactive") {
    return;
  }

  // Initialize map if needed
  if (!guestCardMap[propertyId]) {
    guestCardMap[propertyId] = {
      total: 0,
      active: 0,
      applicationCompleted: 0,
    };
  }

  // If the status is either "active" or "application completed," count it
  if (status === "active") {
    guestCardMap[propertyId].active += 1;
    guestCardMap[propertyId].total += 1;
  } else if (status === "application completed") {
    guestCardMap[propertyId].applicationCompleted += 1;
    guestCardMap[propertyId].total += 1;
  }
});

//
// 4) Create the output array for each property
//
const output = Object.keys(guestCardMap).map(propertyId => {
  const data = guestCardMap[propertyId];
  return {
    "Property ID": propertyId,
    "Total Guest Cards (Active + Completed)": data.total,
    "Active": data.active,
    "Application Completed": data.applicationCompleted
  };
});

//
// 5) Return the final results
//
return output;
