// 1) Get data from input
let allItems = $input.all();
let rows = allItems.map(item => item.json);

// 2) Initialize an object to accumulate totals by property_id
const propertyMap = {};

// 3) Loop through each row
rows.forEach(row => {
    // Convert property_id to integer if possible
    if (row["property_id"] !== undefined) {
        const parsedPropId = parseInt(row["property_id"], 10);
        if (!isNaN(parsedPropId)) {
            row["property_id"] = parsedPropId;
        }
    }

    // Parse numeric fields
    if (row["total_receivable"] !== undefined && row["total_receivable"] !== null) {
        row["total_receivable"] = parseFloat(
            row["total_receivable"].toString().replace(/,/g, "")
        ) || 0;
    } else {
        row["total_receivable"] = 0;
    }

    if (row["delinquent_rent"] !== undefined && row["delinquent_rent"] !== null) {
        row["delinquent_rent"] = parseFloat(
            row["delinquent_rent"].toString().replace(/,/g, "")
        ) || 0;
    } else {
        row["delinquent_rent"] = 0;
    }

    if (row["delinquent_subsidy"] !== undefined && row["delinquent_subsidy"] !== null) {
        row["delinquent_subsidy"] = parseFloat(
            row["delinquent_subsidy"].toString().replace(/,/g, "")
        ) || 0;
    } else {
        row["delinquent_subsidy"] = 0;
    }

    if (row["monthly_charges"] !== undefined && row["monthly_charges"] !== null) {
        row["monthly_charges"] = parseFloat(
            row["monthly_charges"].toString().replace(/,/g, "")
        ) || 0;
    } else {
        row["monthly_charges"] = 0;
    }

    if (row["last_payment_amount"] !== undefined && row["last_payment_amount"] !== null) {
        row["last_payment_amount"] = parseFloat(
            row["last_payment_amount"].toString().replace(/,/g, "")
        ) || 0;
    } else {
        row["last_payment_amount"] = 0;
    }

    if (row["monthly_rent"] !== undefined && row["monthly_rent"] !== null) {
        row["monthly_rent"] = parseFloat(
            row["monthly_rent"].toString().replace(/,/g, "")
        ) || 0;
    } else {
        row["monthly_rent"] = 0;
    }

    if (row["delinquent_fees"] !== undefined && row["delinquent_fees"] !== null) {
        row["delinquent_fees"] = parseFloat(
            row["delinquent_fees"].toString().replace(/,/g, "")
        ) || 0;
    } else {
        row["delinquent_fees"] = 0;
    }

    // Now accumulate into propertyMap
    const propId = row["property_id"];
    if (propId === undefined || isNaN(propId)) {
        // Skip rows that don't have a valid numeric property ID
        return;
    }

    // Initialize if we haven't seen this property yet
    if (!propertyMap[propId]) {
        propertyMap[propId] = {
            property_id: propId,
            total_receivable: 0,
            delinquent_rent: 0,
            delinquent_subsidy: 0,
            monthly_charges: 0,
            last_payment_amount: 0,
            monthly_rent: 0,
            delinquent_fees: 0
        };
    }

    // Sum up numeric fields
    propertyMap[propId].total_receivable += row.total_receivable;
    propertyMap[propId].delinquent_rent += row.delinquent_rent;
    propertyMap[propId].delinquent_subsidy += row.delinquent_subsidy;
    propertyMap[propId].monthly_charges += row.monthly_charges;
    propertyMap[propId].last_payment_amount += row.last_payment_amount;
    propertyMap[propId].monthly_rent += row.monthly_rent;
    propertyMap[propId].delinquent_fees += row.delinquent_fees;
});

// 4) Convert propertyMap back into an array, rounding to two decimals
const output = Object.values(propertyMap).map(item => {
    // Round each numeric field to nearest hundredth (dollar format)
    item.total_receivable = parseFloat(item.total_receivable.toFixed(2));
    item.delinquent_rent = parseFloat(item.delinquent_rent.toFixed(2));
    item.delinquent_subsidy = parseFloat(item.delinquent_subsidy.toFixed(2));
    item.monthly_charges = parseFloat(item.monthly_charges.toFixed(2));
    item.last_payment_amount = parseFloat(item.last_payment_amount.toFixed(2));
    item.monthly_rent = parseFloat(item.monthly_rent.toFixed(2));
    item.delinquent_fees = parseFloat(item.delinquent_fees.toFixed(2));

    return { json: item };
});

// 5) Return final aggregated data (one item per property)
return output;

