if (!Array.isArray($input.all())) {
    throw new Error("Input is not an array. Ensure the previous node outputs a JSON array.");
}

const items = $input.all();

const output = items.map(item => {
    const row = { ...item.json };

    const totalRent = parseFloat(row["total_rent"] || 0);
    const delinquentRent = parseFloat(row["delinquent_rent"] || 0);
    const delinquentSubsidy = parseFloat(row["delinquent_subsidy"] || 0);
    const delinquentFees = parseFloat(row["delinquent_fees"] || 0);

    // Calculate totalDelinquent and totalReceivable
    const totalDelinquent = delinquentRent + delinquentSubsidy + delinquentFees;
    const totalReceivable = totalDelinquent; // total receivable is the same as total delinquent in this case.

    // 1. Delinquency as a percentage of total_receivable
    const percentOfReceivable = totalReceivable > 0 ? ((totalReceivable / totalRent) * 100) : 0;
    row["delinquency_percent_receivable"] = parseFloat(percentOfReceivable.toFixed(2));

    // 2. Delinquency as a percentage of total_rent only
    const percentOfRentOnly = totalRent > 0 ? (((delinquentSubsidy + delinquentRent) / totalRent) * 100) : 0;
    row["delinquency_percent_rent_only"] = parseFloat(percentOfRentOnly.toFixed(2));

    // 3. Delinquency as a percentage of total_rent without subsidies
    const delinquentWithoutSubsidy = delinquentRent + delinquentFees;
    const percentWithoutSubsidies = totalRent > 0 ? ((delinquentWithoutSubsidy / totalRent) * 100) : 0;
    row["delinquency_percent_no_subsidies"] = parseFloat(percentWithoutSubsidies.toFixed(2));

    return { json: row };
});

return output;

