let emailBody = `
    <h2>Delinquency Report</h2>
    <p>This report details delinquency for the properties to date as a percentage of occupied units.</p>
    <p>If a property is not represented, it either has no delinquency or no data available.</p>
    <table border='1' cellpadding='5' cellspacing='0' style='border-collapse: collapse;'>
        <tr>
            <th>Property</th>
            <th>Total Rent</th>
            <th>Total Delinquent</th>
            <th>Delinquency % (Receivable)</th>
            <th>Delinquency % (Rent Only)</th>
        </tr>`;

items.forEach(item => {
    let row = item.json;

    let totalRent = parseFloat(row.total_rent || 0);
    let totalDelinquent = parseFloat(row.delinquent_rent || 0) + parseFloat(row.delinquent_fees || 0);

    let dpReceivable = parseFloat(row.delinquency_percent_receivable || 0);
    let dpRentOnly = parseFloat(row.delinquency_percent_rent_only || 0);

    let delinquencyColor = "black";
    if (dpReceivable > 10) {
        delinquencyColor = "red";
    } else if (dpReceivable >= 5) {
        delinquencyColor = "orange";
    } else {
        delinquencyColor = "green";
    }

    let delinquentRentColor = (totalDelinquent > 5000) ? "orange" : "black";

    // Add gold stars
    let dpReceivableDisplay = dpReceivable.toFixed(2) + "%";
    if (dpReceivable < 1) {
        dpReceivableDisplay += " ⭐";
    }

    let dpRentOnlyDisplay = dpRentOnly.toFixed(2) + "%";
    if (dpRentOnly < 1) {
        dpRentOnlyDisplay += " ⭐";
    }

    emailBody += `<tr>
        <td>${row.property_name || "Unknown"}</td>
        <td>$${totalRent.toLocaleString()}</td>
        <td style="color: ${delinquentRentColor};">$${totalDelinquent.toLocaleString()}</td>
        <td style="color: ${delinquencyColor};">${dpReceivableDisplay}</td>
        <td style="color: ${delinquencyColor};">${dpRentOnlyDisplay}</td>
    </tr>`;
});

emailBody += `</table>`;

return [{ json: { emailBody } }];
