// Monday.com User ID for Brandon Stewart
const brandonUserId = "36641322"; 

// Extract the output from OpenAI node
let rawOutput = $item(0).$node["OpenAI"].json["output"];

// Remove markdown formatting (backticks and "json" tag)
rawOutput = rawOutput.replace(/```json\n|\n```/g, "").trim();

// Attempt to parse JSON
let tasks;
try {
    tasks = JSON.parse(rawOutput);
} catch (error) {
    throw new Error("Failed to parse JSON from OpenAI output. The format might be incorrect.");
}

// Ensure tasks is an array
if (!Array.isArray(tasks)) {
    throw new Error(`Invalid task data format. Expected an array but got ${typeof tasks}.`);
}

// Get the current date in YYYY-MM-DD format
const currentDate = new Date().toISOString().split("T")[0];

// Convert tasks into Monday.com items
return tasks.map(task => ({
    json: {
        task: task.task || "Untitled Task", // Task name
        output: JSON.stringify({
            person: brandonUserId, // Assign to Brandon Stewart
            status: "New", // Default status
            date4: { "date": currentDate }, // Date added
            date_mknr4541: task.due_date ? { "date": task.due_date } : {}, // Due Date
            text_mknr1dym: task.client || "" // Client (New column, defaults to empty if not provided)
        })
    }
}));
