// Extract values from Set Node
const property = $json["property"] || "Unknown Property";
const firstName = $json["firstName"] || "Unknown";
const lastName = $json["lastName"] || "Unknown";
const address = $json["address"] || "No Address";
const moveOutDate = $json["moveOutDate"] || null;
const reason = $json["reason"] || "No Reason Given";
const forwardingAddress = $json["forwardingAddress"] || "No Forwarding Address";
const securityDepositReturn = $json["securityDepositReturnMethod"] || "Unknown";
const submittedDate = $json["submittedDateTime"] || null;

// Convert move-out date to Monday.com format (YYYY-MM-DD)
let formattedMoveOutDate = null;
if (moveOutDate) {
    const dateParts = moveOutDate.split('/');
    if (dateParts.length === 3) {
        formattedMoveOutDate = `${dateParts[2]}-${dateParts[0].padStart(2, '0')}-${dateParts[1].padStart(2, '0')}`;
    }
}

// Construct JSON object for Monday.com
return [
  {
    json: {
      itemName: `${property} - ${firstName} ${lastName}`,
      columnValues: JSON.stringify({  // <-- Convert to JSON string
        "text_mknfdxg0": `${firstName} ${lastName}`,  // Resident Name
        "text_mknfm8c8": property,  // Property Name
        "date4": formattedMoveOutDate ? { "date": formattedMoveOutDate } : null,  // Move-out Date
        "text_mknf4476": address,  // Address
        "text_mknfsn9v": reason,  // Move-out Reason
        "text_mknfkj1x": forwardingAddress,  // Forwarding Address
        "status": { "index": 0 },  // Status: "Working on it"
        "boolean_mknfa9m0": { "checked": securityDepositReturn.toLowerCase() === "yes" }  // eReturn (Yes/No)
      })
    }
  }
];

// Extract email text from n8n JSON input
const emailText = $json["text"];

// Regex patterns for extracting information
const namePattern = /Tenant Name:\s*([\w\s-]+)\n/;
const propertyPattern = /Property:\s*([\w\s-]+)\n/;
const addressPattern = /Address - Unit:\s*([\w\s.,-]+)\n/;
const moveOutDatePattern = /Desired Move Out Date:\s*([\d/]+)\n/;
const reasonPattern = /Move Out Reason:\s*(.+)\n/;
const forwardingAddressPattern = /Forwarding Address:\s*([\w\s.,-]+)\n/;
const securityDepositPattern = /Security Deposit Returned via eCheck:\s*(\w+)\n/;
const submittedDatePattern = /Request Submitted On:\s*([\d/: ]+\w{2})\n/;

// Extract values using regex
const nameMatch = emailText.match(namePattern);
const propertyMatch = emailText.match(propertyPattern);
const addressMatch = emailText.match(addressPattern);
const moveOutDateMatch = emailText.match(moveOutDatePattern);
const reasonMatch = emailText.match(reasonPattern);
const forwardingAddressMatch = emailText.match(forwardingAddressPattern);
const securityDepositMatch = emailText.match(securityDepositPattern);
const submittedDateMatch = emailText.match(submittedDatePattern);

// Function to capitalize first letter of each word
const capitalize = (str) => str.replace(/\b\w/g, c => c.toUpperCase());

// Split full name into first and last name
const fullName = nameMatch ? nameMatch[1].trim() : "Unknown Tenant";
const [firstName, ...lastNameParts] = fullName.split(" ");
const lastName = lastNameParts.join(" ").trim();

// Construct extracted data object
return [
  {
    json: {
      firstName: capitalize(firstName || "Unknown"),
      lastName: capitalize(lastName || "Unknown"),
      property: capitalize(propertyMatch ? propertyMatch[1].trim() : "Unknown Property"),
      address: addressMatch ? addressMatch[1].trim() : "No Address",
      moveOutDate: moveOutDateMatch ? moveOutDateMatch[1].trim() : null,
      reason: reasonMatch ? reasonMatch[1].trim() : "No Reason Given",
      forwardingAddress: forwardingAddressMatch ? forwardingAddressMatch[1].trim() : "No Forwarding Address",
      securityDepositReturnMethod: securityDepositMatch ? securityDepositMatch[1].trim() : "Unknown",
      submittedDateTime: submittedDateMatch ? submittedDateMatch[1].trim() : null
    }
  }
];
