return items.map(item => {
  const data = item.json;

  const recordExists = !!data.tenant_id; // use any reliable field unique to the match

  return {
    json: {
      ...data,
      record_found: recordExists
    }
  };
});

return items.map(item => {
  const output = item.json.output || {};
  let address = output.address || '';
  let phone = output.phone || '';

  // Strip phone down to digits only
  const phoneDigits = phone.replace(/\D/g, '');

  // Clean up address
  address = address.toLowerCase().replace(/\s+/g, ' ').trim();
  address = address.replace(/\b(unit|apt|apartment|#)\b\s*\w*/gi, ''); // remove unit info
  address = address
    .replace(/\b(street|st|avenue|ave|road|rd|drive|dr|court|ct|lane|ln|trail|trl|boulevard|blvd|place|pl|terrace|ter|circle|cir|way)\b\.?/gi, '')
    .replace(/[^\w\s]/g, '') // remove punctuation
    .replace(/\s+/g, ' ') // collapse spaces
    .trim();

  const parts = address.split(' ');
  const addressNumber = parts[0] || '';
  const addressStreet = parts.slice(1).join(' ') || '';

  const fullName = output.name || '';
  const nameParts = fullName.trim().split(/\s+/);
  const firstName = nameParts.slice(0, -1).join(' ');
  const lastName = nameParts.slice(-1).join(' ');

  return {
    json: {
      ...output,
      phone: phoneDigits,
      first_name: firstName,
      last_name: lastName,
      address_number: addressNumber,
      address_street: addressStreet,
      address: address
    }
  };
});

const lead = $json;
const managers = $items("Get Managers");

// Extract fields from lead
const propertyId = String(lead.Property_ID).trim();
const groupIds = (lead.Property_Group_ID_s_ || "").split(',').map(id => id.trim());
const propertyType = (lead.Property_Type || "").toLowerCase().replace(/[^a-z]/g, ""); // normalize

let matchedManager = null;
let match_type = "none";

// Try to match using group_id first (Single-Family style)
for (const mgr of managers) {
  const mgrJson = mgr.json;
  const mgrGroupIds = (mgrJson.group_id || "").split(',').map(id => id.trim());

  if (groupIds.some(id => mgrGroupIds.includes(id))) {
    matchedManager = mgrJson;
    match_type = "group_id";
    break;
  }
}

// If no group match, try property_id (Multi-Family style)
if (!matchedManager) {
  for (const mgr of managers) {
    const mgrJson = mgr.json;
    const mgrPropertyIds = (mgrJson.property_id || "").split(',').map(id => id.trim());

    if (mgrPropertyIds.includes(propertyId)) {
      matchedManager = mgrJson;
      match_type = "property_id";
      break;
    }
  }
}

// Return updated lead data
return [{
  json: {
    ...lead,
    manager_name: matchedManager
      ? `${matchedManager.manager_first} ${matchedManager.manager_last}`
      : "Unknown",
    manager_email: matchedManager
      ? matchedManager.manager_email
      : "brandon@blazeproperty.com",
    match_type
  }
}];

