const input = $json;
const possibleMatches = $items("Property Lookup"); // Adjust if your node is named differently

const leadStreet = (input.address_street || "").toLowerCase().trim();

let bestMatch = null;

for (const match of possibleMatches) {
  const propertyStreet = (match.json.Property_Street_Address_1 || "")
    .toLowerCase()
    .replace(/[^a-z\s]/gi, '')
    .trim();

  // Break into words and compare
  const streetWords = propertyStreet.split(' ').slice(1).join(' ');
  if (leadStreet.startsWith(streetWords)) {
    bestMatch = match.json;
    break;
  }
}

// Return best match or fallback to the first one (or none)
if (bestMatch) {
  return [{
    json: {
      ...input,
      ...bestMatch,
      match_level: "exact"
    }
  }];
} else if (possibleMatches.length > 0) {
  return [{
    json: {
      ...input,
      ...possibleMatches[0].json,
      match_level: "fallback"
    }
  }];
} else {
  return [{
    json: {
      ...input,
      match_level: "none"
    }
  }];
}
