    ) || 0;
  } else {
    row["Amount Receivable"] = 0;
  }

  if (row["Delinquent Rent"] !== undefined) {
    row["Delinquent Rent"] = parseFloat(
      row["Delinquent Rent"].toString().replace(/,/g, "")
    ) || 0;
  } else {
    row["Delinquent Rent"] = 0;
  }

  if (row["Delinquent Subsidy Amount"] !== undefined) {
    row["Delinquent Subsidy Amount"] = parseFloat(
      row["Delinquent Subsidy Amount"].toString().replace(/,/g, "")
    ) || 0;
  } else {
    row["Delinquent Subsidy Amount"] = 0;
  }

  // Now accumulate into propertyMap
  const propId = row["Property ID"];
  if (propId === undefined || isNaN(propId)) {
    // Skip rows that don't have a valid numeric property ID
    return;
  }

  // Initialize if we haven't seen this property yet
  if (!propertyMap[propId]) {
    propertyMap[propId] = {
      "Property ID": propId,
      "Amount Receivable": 0,
      "Delinquent Rent": 0,
      "Delinquent Subsidy Amount": 0,
      // Add other fields you want to track or sum up
    };
  }

  // Sum up numeric fields
  propertyMap[propId]["Amount Receivable"] += row["Amount Receivable"];
  propertyMap[propId]["Delinquent Rent"] += row["Delinquent Rent"];
  propertyMap[propId]["Delinquent Subsidy Amount"] += row["Delinquent Subsidy Amount"];
});

