rentData.forEach(row => {
  let property = row.Property;
  let rent = parseFloat(row.Rent) || 0;
  if (property) {
    rentTotals[property] = (rentTotals[property] || 0) + rent;
  }
});

// Aggregate delinquency totals by property
delinquencyData.forEach(row => {
  let property = row.Property;
  let amountReceivable = parseFloat(row["Amount Receivable"].replace(',', '')) || 0;
  if (property) {
    delinquencyTotals[property] = (delinquencyTotals[property] || 0) + amountReceivable;
  }
});

// Generate the final report
let reportData = Object.keys(rentTotals).map(property => {
  let rentTotal = rentTotals[property] || 0;
  let delinquentTotal = delinquencyTotals[property] || 0;
  let delinquencyPercentage = rentTotal > 0 ? ((delinquentTotal / rentTotal) * 100).toFixed(2) + '%' : '0%';
  return {
    Property: property,
    "Total Rent": rentTotal,
    "Delinquent Rent": delinquentTotal,
    "Delinquency Percentage": delinquencyPercentage
  };
});

return [{ json: reportData }];

