const today = new Date();
const dayOfMonth = today.getDate(); // Get today's day of the month
const dayOfWeek = today.getDay(); // Get today's day of the week (0 = Sunday, 1 = Monday, etc.)

// Retrieve all static reminders
const staticReminders = $items().map(item => item.json);

// Filter reminders that match either today's day of the month or day of the week
const filteredStaticReminders = staticReminders.filter(reminder => {
    // Convert day and week_day to integers for comparison, defaulting to -1 if undefined
    const reminderDayOfMonth = parseInt(reminder.day) || -1;
    const reminderDayOfWeek = parseInt(reminder.week_day) || -1;

    // Match either today's day of the month or day of the week
    return reminderDayOfMonth === dayOfMonth || reminderDayOfWeek === dayOfWeek;
});

// Return all filtered reminders
return filteredStaticReminders.length > 0 ? filteredStaticReminders : [{ json: {} }];

const today = new Date();
const todayDate = `${today.getMonth() + 1}/${today.getDate()}/${today.getFullYear()}`; // Format as MM/DD/YYYY

// Retrieve all variable reminders
const variableReminders = $items().map(item => item.json);

// Filter reminders that match todayâ€™s date
const filteredVariableReminders = variableReminders.filter(reminder => {
    return reminder.reminder_date === todayDate;
});

// Return all filtered reminders
return filteredVariableReminders.length > 0 ? filteredVariableReminders : [{ json: {} }];

// Get all reminders from the merged node output
const mergedReminders = $items().map(item => item.json);

// Check if there are any reminders
if (mergedReminders.length === 0) {
    // If no reminders, return an empty output to prevent email sending
    return [];
}

// Initialize the email content
const todayDate = new Date().toISOString().split('T')[0];
let emailContent = `<h2>Daily Reminders for ${todayDate}</h2>`;

// Add each reminder to the email content
mergedReminders.forEach(reminder => {
    // Ensure that both title and description are available, falling back to "No Title" and "No Description" if needed
    const title = reminder.reminder_title || "No Title";
    const description = reminder.reminder_description || "No Description";
    emailContent += `<p><strong>${title}</strong>: ${description}</p>`;
});

// Static message including an unordered list for daily action items
const staticMessage = `
<p><strong>Note:</strong> Daily Action Items:</p>
<ul>
<li><strong>Report Review</strong> - You should be reviewing the following reports daily - Delinquency, Rent Roll, Receivables Activity, Guest Cards, Applications</li>
    <li><strong>Post Facebook</strong> - Daily Facebook posts are required on both the property page and in relevant Buy/Sell Groups.</li>
    <li><strong>Review Applications</strong> - Review any applications that are pending and complete any action steps (e.g., landlord verification, employment verification, etc).</li>
    <li><strong>Review Make-Ready Board</strong> - Update the status of any changes of units on the make ready board.</li>
    <li><strong>Walk Property</strong> - As the manager of the property, you should be walking the property on a daily basis to familiarize yourself with any outstanding items that need your attention.</li>
    <li><strong>Guest Cards</strong> - Contact any pending or new guest cards and send applications to all contacts for that unit.</li>
</ul>
<p><strong>Note:</strong> All items above are expected to be completed on time. If a link is provided to instructions or other documentation, please review that before completing work.</p>`;

// Append the static message to the email content
emailContent += staticMessage;

// Return email content for the Send Email node
return { json: { emailContent } };
