function calculateStars(ratings) {
  const total = ratings.reduce((acc, rating) => acc + rating, 0);
  const average = total / ratings.length;
  return Math.round(average);
}

function generateFeedback(responses, comments) {
  const feedback = [];

  if (responses['ctw44'] < 4) {
    feedback.push("Improve the overall move-in experience by ensuring all paperwork is clear and assistance is available for any questions.");
  }

  if (responses['a4clj'] < 4) {
    feedback.push("Ensure the condition of the unit meets tenants' expectations before move-in by conducting thorough inspections and addressing any issues promptly.");
  }

  if (responses['6g3zv'] < 4) {
    feedback.push("Enhance the interactions with the property management team by providing additional training on customer service and responsiveness.");
  }

  if (responses['y78xb'] < 4) {
    feedback.push("Maintain cleanliness and upkeep of common areas regularly to meet tenants' standards.");
  }

  if (feedback.length === 0) {
    feedback.push("Thank you for your positive feedback! We strive to maintain high standards and continuously improve our services.");
  } else {
    feedback.push("We value your feedback and will work on the mentioned areas to enhance your living experience.");
  }

  if (comments) {
    feedback.push(`Additional Comments: ${comments}`);
  }

  return feedback.join(" ");
}

// Collect responses
const responses = {
  'ctw44': parseInt($json["body"]['[ctw44]'], 10),
  'a4clj': parseInt($json["body"]['[a4clj]'], 10),
  '6g3zv': parseInt($json["body"]['[6g3zv]'], 10),
  'y78xb': parseInt($json["body"]['[y78xb]'], 10)
};

// Check for any null values and handle them appropriately
const ratings = Object.values(responses).filter(rating => !isNaN(rating));
const comments = $json["body"]['[ghrqk]'] || "";

const stars = calculateStars(ratings);
const feedback = generateFeedback(responses, comments);

return [{
  json: {
    "Stars": stars,
    "Feedback": feedback
  }
}];
