const raw = $input.item.json.data;
if (!raw) throw new Error("No application data found.");

const lines = raw.split('\n').filter(l => l.trim());
const headers = lines[0].split(',').map(h => h.trim());
const data = lines.slice(1);

const results = {};

for (const line of data) {
  const fields = line.split(/,(?=(?:(?:[^\"]*\"){2})*[^\"]*$)/).map(v => v.replace(/^\"|\"$/g, '').trim());
  const entry = Object.fromEntries(headers.map((h, i) => [h, fields[i]]));

  const propId = parseInt(entry["Property ID"]);
  const status = entry["Status"] || "Unknown";

  if (!isNaN(propId)) {
    if (!results[propId]) {
      results[propId] = {
        property_id: propId,
        application_count: 0,
        application_statuses: {}
      };
    }

    results[propId].application_count++;
    results[propId].application_statuses[status] = (results[propId].application_statuses[status] || 0) + 1;
  }
}

return Object.values(results).map(item => ({
  json: {
    property_id: item.property_id,
    application_count: item.application_count,
    application_statuses: JSON.stringify(item.application_statuses)
  }
}));
