// Helper function to convert Excel date code to MM/DD/YYYY format
function excelDateToJSDate(serial) {
    if (!serial || isNaN(serial)) return ""; // Handle undefined, null, or non-numeric values
    const jsDate = new Date((serial - 25569) * 86400000);
    const month = String(jsDate.getMonth() + 1).padStart(2, '0'); // Months are zero-based
    const day = String(jsDate.getDate()).padStart(2, '0');
    const year = jsDate.getFullYear();
    return `${month}/${day}/${year}`;
}

// Extract first valid email from a comma-separated string
function getFirstValidEmail(emails) {
    if (!emails || typeof emails !== "string") return ""; // Handle missing, null, or non-string values
    const emailList = emails.split(',').map(email => email.trim()).filter(email => email.includes("@"));
    return emailList.length > 0 ? emailList[0] : "";
}

// Extract only numeric phone number and format it as (XXX) XXX-XXXX
function cleanPhoneNumber(phoneNumber) {
    if (!phoneNumber || typeof phoneNumber !== "string") return ""; // Handle missing, null, or non-string values
    const digits = phoneNumber.replace(/\D/g, ""); // Remove all non-numeric characters
    if (digits.length === 10) {
        return `(${digits.substring(0, 3)}) ${digits.substring(3, 6)}-${digits.substring(6, 10)}`;
    }
    return ""; // Return empty if not a valid 10-digit number
}

// Process input data
const items = $input.all().map(item => {
    let data = item.json;

    return {
        json: {
            ...data,
            "Phone Numbers": data["Phone Numbers"] ? cleanPhoneNumber(data["Phone Numbers"]) : "",
            "Move-in": data["Move-in"] ? excelDateToJSDate(data["Move-in"]) : "",
            "Lease From": data["Lease From"] ? excelDateToJSDate(data["Lease From"]) : "",
            "Lease To": data["Lease To"] ? excelDateToJSDate(data["Lease To"]) : "",
            "Tenant Birthdate": data["Tenant Birthdate"] ? excelDateToJSDate(data["Tenant Birthdate"]) : "",
            "Emails": data["Emails"] ? getFirstValidEmail(data["Emails"]) : "",
        }
    };
});

// Return cleaned data
return items;
