const items = $input.all();
const grouped = {};

// Step 1: Group by property_id + week_ending and aggregate metrics
for (const item of items) {
  const {
    property_id,
    property_name,
    owner_id,
    owner_email,
    week_ending,
    ...metrics
  } = item.json;

  const weekKey = `${property_id}_${week_ending}`;
  if (!grouped[weekKey]) {
    grouped[weekKey] = {
      property_id,
      property_name,
      owner_id,
      owner_email,
      week_ending: new Date(week_ending),
      guest_cards: 0,
      applications: 0,
      showings: 0,
      showings_cancelled: 0,
      lead_sources: {},
      application_statuses: {}
    };
  }

  // Sum numeric metrics
  grouped[weekKey].guest_cards += metrics.guest_cards || 0;
  grouped[weekKey].applications += metrics.applications || 0;
  grouped[weekKey].showings += metrics.showings || 0;
  grouped[weekKey].showings_cancelled += metrics.showings_cancelled || 0;

  // Merge JSON fields
  const mergeJSON = (target, sourceStr) => {
    try {
      const source = JSON.parse(sourceStr || '{}');
      for (const key in source) {
        target[key] = (target[key] || 0) + source[key];
      }
    } catch (e) {}
  };

  mergeJSON(grouped[weekKey].lead_sources, metrics.lead_sources);
  mergeJSON(grouped[weekKey].application_statuses, metrics.application_statuses);
}

// Step 2: Regroup by property and sort week data descending
const byProperty = {};

for (const record of Object.values(grouped)) {
  const { property_id } = record;
  if (!byProperty[property_id]) byProperty[property_id] = [];
  byProperty[property_id].push(record);
}

const result = [];

for (const propRecords of Object.values(byProperty)) {
  const sorted = propRecords.sort((a, b) => a.week_ending - b.week_ending);
  const current = sorted[0];
  const last = sorted[1] || {};

  result.push({
    json: {
      property_id: current.property_id,
      property_name: current.property_name,
      owner_id: current.owner_id,
      owner_email: current.owner_email,
      week_ending_current: current.week_ending.toISOString().split("T")[0],
      week_ending_last: last.week_ending
        ? last.week_ending.toISOString().split("T")[0]
        : null,

      guest_cards_current: current.guest_cards,
      guest_cards_last: last.guest_cards || 0,
      applications_current: current.applications,
      applications_last: last.applications || 0,
      showings_current: current.showings,
      showings_last: last.showings || 0,
      showings_cancelled_current: current.showings_cancelled,
      showings_cancelled_last: last.showings_cancelled || 0,

      lead_sources_current: JSON.stringify(current.lead_sources),
      lead_sources_last: JSON.stringify(last.lead_sources || {}),
      application_statuses_current: JSON.stringify(current.application_statuses),
      application_statuses_last: JSON.stringify(last.application_statuses || {})
    }
  });
}

return result;

const item = $input.item.json;

function getDeltaDetails(current, last) {
  const delta = current - last;
  const symbol = delta === 0 ? "Â±" : delta > 0 ? "+" : "";
  const icon = delta === 0 ? "âž–" : delta > 0 ? "ðŸ“ˆ" : "ðŸ“‰";
  const color = delta === 0 ? "#6c757d" : delta > 0 ? "#28a745" : "#dc3545"; // gray, green, red
  return {
    delta: `${symbol}${delta}`,
    icon,
    color
  };
}

const guestCards = getDeltaDetails(item.guest_cards_current, item.guest_cards_last);
const applications = getDeltaDetails(item.applications_current, item.applications_last);
const showings = getDeltaDetails(item.showings_current, item.showings_last);
const showingsCancelled = getDeltaDetails(item.showings_cancelled_current, item.showings_cancelled_last);

return {
  json: {
    ...item,
    guest_cards_delta: guestCards.delta,
    guest_cards_icon: guestCards.icon,
    guest_cards_color: guestCards.color,

    applications_delta: applications.delta,
    applications_icon: applications.icon,
    applications_color: applications.color,

    showings_delta: showings.delta,
    showings_icon: showings.icon,
    showings_color: showings.color,

    showings_cancelled_delta: showingsCancelled.delta,
    showings_cancelled_icon: showingsCancelled.icon,
    showings_cancelled_color: showingsCancelled.color
  }
};

const sources = JSON.parse($json.lead_sources_current || '{}');

let sourcesHTML = '';
for (const [source, count] of Object.entries(sources)) {
  sourcesHTML += `<li><strong>${source}:</strong> ${count}</li>`;
}

// Assume upcoming_showings is a number or string; update if you have richer data
let upcomingHTML = '';
if ($json.upcoming_showings && $json.upcoming_showings > 0) {
  upcomingHTML = `<p><strong>ðŸ“… Upcoming Showings:</strong> ${$json.upcoming_showings}</p>`;
} else {
  upcomingHTML = `<p><em>Looks quiet now, but weâ€™re just one click away from a showing request. ðŸ“…</em></p>`;
}

return {
  json: {
    ...$json,
    lead_sources_current_parsed: sources,
    lead_sources_html: `<ul>${sourcesHTML}</ul>`,
    upcoming_showings_html: upcomingHTML,
  }
};
